mapper.update:
UPDATE tbl_reply
SET
    reply = #{reply},
    replyer = #{replyer}
WHERE rno = #{rno}

mapper.select:
select count(rno)
from tbl_reply
where bno = #{bno}

select * from (
SELECT /*+index_desc(tbl_reply PK_reply)*/
rownum rn, rno, bno, reply, replyer, replydate, updatedate
FROM tbl_reply
where bno = #{bno}
and rno > 0 
and rownum  <=  #{cri.end}
) where rn >= #{cri.start}
